module AbsFun where

-- Haskell module generated by the BNF converter


newtype Ident = Ident String deriving (Eq,Ord,Show)
data Program =
   Prog [Def]
  deriving (Eq,Ord,Show)

data Def =
   Def Ident [Ident] Exp
  deriving (Eq,Ord,Show)

data Exp =
   EVar Ident
 | EApp Exp Exp
 | EAbs Ident Exp
 | EInt Integer
 | EAdd Exp Exp
 | ESub Exp Exp
 | ELt Exp Exp
 | EIf Exp Exp Exp
  deriving (Eq,Ord,Show)

